    <script type="text/javascript">
        // Scroll to the top
        var scroll_top = $('#scrollTop'),
                did_scroll = false;
        $(window).scroll(function () {
            did_scroll = true;
        });
        setInterval(function () {
            if (did_scroll) {
                did_scroll = false;
                if ($(this).scrollTop() > 100) {
                    scroll_top.fadeIn();
                } else {
                    scroll_top.fadeOut();
                }
            }
        }, 250);

        scroll_top.click(function () {
            $("html, body").animate({scrollTop: 0}, 600);
            return false;
        });
    </script>
